{"windows":"[
    // ===== TERMINAL ENHANCEMENTS =====
    {
        "key": "shift+enter",
        "command": "workbench.action.terminal.sendSequence",
        "args": {
            "text": "\\\r\n"
        },
        "when": "terminalFocus"
    },

    // ===== DEVELOPMENT TASK SHORTCUTS =====
    {
        "key": "ctrl+shift+b",
        "command": "workbench.action.tasks.build",
        "when": "!inDebugMode"
    },
    {
        "key": "ctrl+shift+t",
        "command": "workbench.action.tasks.test",
        "when": "!inDebugMode"
    },
    {
        "key": "ctrl+shift+r",
        "command": "workbench.action.tasks.runTask",
        "args": "üöÄ Dev Server"
    },
    {
        "key": "ctrl+shift+f",
        "command": "workbench.action.tasks.runTask",
        "args": "üé® Format All"
    },
    {
        "key": "ctrl+shift+l",
        "command": "workbench.action.tasks.runTask",
        "args": "üîç Lint All"
    },
    {
        "key": "ctrl+shift+q",
        "command": "workbench.action.tasks.runTask",
        "args": "üéØ Full Quality Check"
    },
    {
        "key": "ctrl+shift+d",
        "command": "workbench.action.tasks.runTask",
        "args": "üöÄ Deploy Ready"
    },
    {
        "key": "ctrl+shift+c",
        "command": "workbench.action.tasks.runTask",
        "args": "üßπ Clean"
    },
    {
        "key": "ctrl+shift+i",
        "command": "workbench.action.tasks.runTask",
        "args": "üì¶ Install Dependencies"
    },
    {
        "key": "ctrl+shift+p",
        "command": "workbench.action.tasks.runTask",
        "args": "üöÄ Start Production"
    },

    // ===== COPILOT & CONFIGURATION SHORTCUTS =====
    {
        "key": "ctrl+alt+g",
        "command": "workbench.action.openSettings",
        "args": "@ext:github.copilot"
    },
    {
        "key": "ctrl+alt+c",
        "command": "workbench.action.files.openFile",
        "args": "${workspaceFolder}/.github/copilot-instructions.md"
    },
    {
        "key": "ctrl+alt+m",
        "command": "workbench.action.files.openFolder", 
        "args": "${workspaceFolder}/.github/chatmodes"
    }
]"}
