{"content": "# ðŸ§  Biome Extension Memory Monitor
# PowerShell script to track VS Code extension memory usage

param(
    [int]$IntervalSeconds = 30,
    [int]$DurationMinutes = 60,
    [string]$LogFile = "biome-memory-monitor.log"
)

Write-Host "ðŸ§  Starting Biome Extension Memory Monitor" -ForegroundColor Green
Write-Host "â° Monitoring for $DurationMinutes minutes, checking every $IntervalSeconds seconds" -ForegroundColor Yellow
Write-Host "ðŸ“ Log file: $LogFile" -ForegroundColor Cyan

# Initialize log file
$timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
"=== Biome Memory Monitor Started at $timestamp ===" | Out-File $LogFile -Encoding UTF8

$endTime = (Get-Date).AddMinutes($DurationMinutes)
$iteration = 0

while ((Get-Date) -lt $endTime) {
    $iteration++
    $currentTime = Get-Date -Format "yyyy-MM-dd HH:mm:ss"

    try {
        # Get VS Code processes
        $vscodeProcesses = Get-Process -Name "Code*" -ErrorAction SilentlyContinue

        if ($vscodeProcesses) {
            $totalMemoryMB = 0
            $biomeMemoryMB = 0

            foreach ($process in $vscodeProcesses) {
                $memoryMB = [math]::Round($process.WorkingSet64 / 1MB, 2)
                $totalMemoryMB += $memoryMB

                # Check if this is likely the Biome extension process
                $windowTitle = $process.MainWindowTitle
                if ($windowTitle -like "*biome*" -or $process.ProcessName -like "*biome*") {
                    $biomeMemoryMB += $memoryMB
                }
            }

            # Log current memory usage
            $logEntry = "[$currentTime] Iteration $iteration | VS Code Total: ${totalMemoryMB}MB | Biome-related: ${biomeMemoryMB}MB"
            Write-Host $logEntry -ForegroundColor $(if ($totalMemoryMB -gt 1000) { "Red" } elseif ($totalMemoryMB -gt 500) { "Yellow" } else { "Green" })

            $logEntry | Out-File $LogFile -Append -Encoding UTF8

            # Alert if memory usage is high
            if ($totalMemoryMB -gt 2000) {
                Write-Host "âš ï¸  HIGH MEMORY USAGE DETECTED! VS Code using ${totalMemoryMB}MB" -ForegroundColor Red
                "âš ï¸  HIGH MEMORY USAGE: ${totalMemoryMB}MB at $currentTime" | Out-File $LogFile -Append -Encoding UTF8
            }

            if ($biomeMemoryMB -gt 200) {
                Write-Host "ðŸ”´ BIOME MEMORY TARGET EXCEEDED! Using ${biomeMemoryMB}MB (Target: <200MB)" -ForegroundColor Red
                "ðŸ”´ BIOME MEMORY EXCEEDED: ${biomeMemoryMB}MB at $currentTime" | Out-File $LogFile -Append -Encoding UTF8
            }

        } else {
            Write-Host "[$currentTime] VS Code not running" -ForegroundColor Gray
            "[$currentTime] VS Code not running" | Out-File $LogFile -Append -Encoding UTF8
        }

    } catch {
        $errorMsg = "[$currentTime] Error monitoring memory: $($_.Exception.Message)"
        Write-Host $errorMsg -ForegroundColor Red
        $errorMsg | Out-File $LogFile -Append -Encoding UTF8
    }

    Start-Sleep -Seconds $IntervalSeconds
}

$endTimestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
Write-Host "âœ… Memory monitoring completed at $endTimestamp" -ForegroundColor Green
"=== Biome Memory Monitor Ended at $endTimestamp ===" | Out-File $LogFile -Append -Encoding UTF8

# Generate summary report
Write-Host "ðŸ“Š Generating summary report..." -ForegroundColor Cyan
$logContent = Get-Content $LogFile
$memoryReadings = $logContent | Where-Object { $_ -match "Iteration \d+" } | ForEach-Object {
    if ($_ -match "VS Code Total: ([\d.]+)MB") {
        [double]$matches[1]
    }
}

if ($memoryReadings.Count -gt 0) {
    $avgMemory = [math]::Round(($memoryReadings | Measure-Object -Average).Average, 2)
    $maxMemory = [math]::Round(($memoryReadings | Measure-Object -Maximum).Maximum, 2)
    $minMemory = [math]::Round(($memoryReadings | Measure-Object -Minimum).Minimum, 2)

    $summary = @"

ðŸ“Š MEMORY USAGE SUMMARY
======================
Monitoring Duration: $DurationMinutes minutes
Total Readings: $($memoryReadings.Count)
Average Memory: ${avgMemory}MB
Peak Memory: ${maxMemory}MB
Minimum Memory: ${minMemory}MB
Target Met: $(if ($maxMemory -lt 200) { "âœ… YES" } else { "âŒ NO - Peak was ${maxMemory}MB" })

"@

    Write-Host $summary -ForegroundColor Green
    $summary | Out-File $LogFile -Append -Encoding UTF8
}

Write-Host "ðŸ“ Complete log saved to: $LogFile" -ForegroundColor Cyan", "path": "d:\neonpro\scripts\monitor-biome-memory.ps1"}

{"content": "# ðŸš€ Biome Performance Validation Commands
# Quick commands to validate memory optimization success

# 1. ðŸ“Š Quick Memory Check (PowerShell)
function Test-BiomeMemory {
    Write-Host "ðŸ§  Checking current VS Code/Biome memory usage..." -ForegroundColor Yellow

    $vscodeProcesses = Get-Process -Name "Code*" -ErrorAction SilentlyContinue
    if ($vscodeProcesses) {
        $totalMemory = ($vscodeProcesses | Measure-Object WorkingSet64 -Sum).Sum / 1MB
        Write-Host "ðŸ“Š Total VS Code Memory: $([math]::Round($totalMemory, 2))MB" -ForegroundColor $(if ($totalMemory -gt 1000) { "Red" } elseif ($totalMemory -gt 500) { "Yellow" } else { "Green" })

        if ($totalMemory -lt 200) {
            Write-Host "âœ… EXCELLENT: Memory usage under 200MB target!" -ForegroundColor Green
        } elseif ($totalMemory -lt 500) {
            Write-Host "âœ… GOOD: Significant improvement from 20GB baseline" -ForegroundColor Green
        } else {
            Write-Host "âš ï¸  Still high - may need additional optimization" -ForegroundColor Yellow
        }
    } else {
        Write-Host "âŒ VS Code not running" -ForegroundColor Red
    }
}

# 2. ðŸ” File Processing Check
function Test-BiomeScope {
    Write-Host "ðŸ” Testing Biome file processing scope..." -ForegroundColor Yellow

    # Test if Biome is respecting ignore patterns
    $testResults = @()

    # Check if node_modules is being ignored
    if (Test-Path "node_modules") {
        $nodeModulesSize = (Get-ChildItem "node_modules" -Recurse -File | Measure-Object Length -Sum).Sum / 1MB
        Write-Host "ðŸ“ node_modules size: $([math]::Round($nodeModulesSize, 2))MB" -ForegroundColor Cyan
        $testResults += "node_modules: $([math]::Round($nodeModulesSize, 2))MB"
    }

    # Check build outputs
    $buildDirs = @(".next", ".turbo", "dist", "build")
    foreach ($dir in $buildDirs) {
        if (Test-Path $dir) {
            $dirSize = (Get-ChildItem $dir -Recurse -File -ErrorAction SilentlyContinue | Measure-Object Length -Sum).Sum / 1MB
            Write-Host "ðŸ“ $dir size: $([math]::Round($dirSize, 2))MB" -ForegroundColor Cyan
            $testResults += "$dir: $([math]::Round($dirSize, 2))MB"
        }
    }

    $totalExcludedSize = ($testResults | ForEach-Object { ($_ -split ": ")[1] -replace "MB", "" } | Measure-Object -Sum).Sum
    Write-Host "ðŸŽ¯ Total excluded content: $([math]::Round($totalExcludedSize, 2))MB" -ForegroundColor Green
    Write-Host "âœ… This content should NOT be processed by Biome" -ForegroundColor Green
}

# 3. âš¡ Performance Validation
function Test-BiomePerformance {
    param([int]$TestDurationSeconds = 60)

    Write-Host "âš¡ Running $TestDurationSeconds second performance test..." -ForegroundColor Yellow

    $startTime = Get-Date
    $startMemory = if (Get-Process -Name "Code*" -ErrorAction SilentlyContinue) {
        (Get-Process -Name "Code*" | Measure-Object WorkingSet64 -Sum).Sum / 1MB
    } else { 0 }

    Write-Host "ðŸ Starting memory: $([math]::Round($startMemory, 2))MB" -ForegroundColor Cyan

    # Simulate typical VS Code usage
    Write-Host "ðŸ”„ Simulating file operations..." -ForegroundColor Gray

    # Wait and monitor
    Start-Sleep -Seconds $TestDurationSeconds

    $endMemory = if (Get-Process -Name "Code*" -ErrorAction SilentlyContinue) {
        (Get-Process -Name "Code*" | Measure-Object WorkingSet64 -Sum).Sum / 1MB
    } else { 0 }

    $memoryChange = $endMemory - $startMemory
    $endTime = Get-Date

    Write-Host "ðŸ Ending memory: $([math]::Round($endMemory, 2))MB" -ForegroundColor Cyan
    Write-Host "ðŸ“ˆ Memory change: $([math]::Round($memoryChange, 2))MB" -ForegroundColor $(if ($memoryChange -gt 50) { "Red" } elseif ($memoryChange -gt 10) { "Yellow" } else { "Green" })

    if ($memoryChange -lt 10) {
        Write-Host "âœ… EXCELLENT: Stable memory usage!" -ForegroundColor Green
    } elseif ($memoryChange -lt 50) {
        Write-Host "âœ… GOOD: Acceptable memory growth" -ForegroundColor Green
    } else {
        Write-Host "âš ï¸  CONCERN: High memory growth detected" -ForegroundColor Yellow
    }
}

# 4. ðŸŽ¯ Complete Validation Suite
function Invoke-BiomeValidation {
    Write-Host "ðŸŽ¯ Running Complete Biome Optimization Validation" -ForegroundColor Green
    Write-Host "=================================================" -ForegroundColor Green

    Test-BiomeMemory
    Write-Host ""
    Test-BiomeScope
    Write-Host ""
    Test-BiomePerformance -TestDurationSeconds 30

    Write-Host ""
    Write-Host "ðŸ† Validation Complete!" -ForegroundColor Green
    Write-Host "Target: <200MB RAM usage | Expected: 80-90% reduction from 20GB baseline" -ForegroundColor Cyan
}

# Export functions for use
Export-ModuleMember -Function Test-BiomeMemory, Test-BiomeScope, Test-BiomePerformance, Invoke-BiomeValidation", "path": "d:\neonpro\scripts\biome-validation.psm1"}

